import React, {useState} from 'react'
import './addPstyle.css'


const AddProduct = ({onAdd}) => {
  const [prod, setProd] = useState({
    author:'',
    name: '',
    qoute: ''
  })
    const submitForm = (e) => {
      e.preventDefault()
      setProd('')     
      onAdd(prod)
    }

//     const formHandler = (e) => {
//       return e.target.value
// }

  return (
    <div>
      <form className='form' onSubmit={submitForm}>
        <input type='text' placeholder='The Author of book' value={prod.author} onChange={e => setProd({author: e.target.value})} />
        <label></label>
        <input type='text' placeholder='Book Name' value={prod.name} onChange={e => setProd({name: e.target.value})} />
        <label></label>
        <input type='text' placeholder='The best quote you remember' value={prod.quote} onChange={e => setProd({qoute: e.target.value})} />
        <button type="submit">Add</button>
      </form>
    </div>
  )
}

export default AddProduct
