import { NavLink } from "react-router-dom";

function Welcome() {
  return (
    <div className="grid place-items-center relative ">
      <div
        style={{ backgroundImage: `url('/images/background.svg')` }}
        className=" flex flex-col items-center justify-center z-10 px-10 h-screen max-w-[30rem] w-full"
      >
        {/* logo */}
        <div>
          <img
            src="/images/HigherEducationLogo.svg"
            className="max-w-full h-auto"
          />
        </div>
        {/* Titles */}
        <p className="text-4xl text-white mt-3 text-center">PROJECT KARMA</p>
        <p className="font-family text-[20px] font-normal text-[#fff]">
          Get rewarded for
        </p>
        <p className="font-family text-[#fff] text-[20px] font-normal">
          telling us what you think.
        </p>
        <NavLink
          to={"../productscan"}
          className="w-full py-2 rounded-2xl bg-[#3B2B2F] text-[25px] text-[#E8E7D8] font-normal mt-7 text-center"
        >
          Scan Barcode to BEGIN
        </NavLink>
      </div>
    </div>
  );
}

export default Welcome;
