import React, { useState } from "react";
import { FaBars } from "react-icons/fa";
import CongratsModal from "./Modals/CongratsModal";
import InfoModal from "./Modals/InfoModal";
import ThanksModal from "./Modals/ThanksModal";

const Spin = () => {
  const [isOpenModal, setOpenModal] = useState(false);
  const [isOpenInfoModal, setOpenInfoModal] = useState(false);
  const [isOpenThanksModal, setOpenThanksModal] = useState(false);

  return (
    <div className="grid place-items-center my-5">
      {isOpenModal && (
        <CongratsModal
          setOpenModal={setOpenModal}
          setOpenInfoModal={setOpenInfoModal}
        />
      )}
      {isOpenInfoModal && (
        <InfoModal
          setOpenInfoModal={setOpenInfoModal}
          setOpenThanksModal={setOpenThanksModal}
        />
      )}
      {isOpenThanksModal && <ThanksModal />}
      {/* container */}
      <div>
        <div className="flex justify-end">
          <FaBars className="text-3xl" />
        </div>
        <div className="flex flex-col justify-center items-center mt-4">
          {/* 
              ///////////Title////////
              ////////////////////////// */}
          <div className="w-[304px] leading-tight">
            <p className="text-[57px] text-[#3B2B2F] text-center ">
              SPIN THE KARMA WHEEL
            </p>
          </div>
          {/* 
               ///////////Imgs////////
               ////////////////////////// */}
          <img
            src="/images/Polygon10.svg"
            className="w-[62px] h-[62px] z-10 mt-2"
          />
          <img src="/images/SpinWheel.svg" className="z-0 mt-[-90px] " />
          {/* 
              ///////////Button////////
              ////////////////////////// */}
          <div className="w-full mt-[-270px] px-5 z-20">
            <button
              className="w-full py-5 bg-[#000000E6] text-[#fff] rounded-[10px] text-[24px] font-bold font-family text-center cursor-pointer] z-20"
              onClick={() => setOpenModal(true)}
            >
              Spin
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Spin;
