import React, { Component } from "react";
import QrReader from "react-qr-scanner";

class QrScanner2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      delay: 100,
      result: "No result",
    };

    this.handleScan = this.handleScan.bind(this);
  }
  handleScan(data) {
    this.setState({
      result: data,
    });
  }
  handleError(err) {
    console.error(err);
  }
  render() {
    const previewStyle = {
      height: "100vh",
      width: "100vw",
    };

    return (
      <div className="grid place-content-center relative">
        <article className="absolute lg:relative md:relative left-0 top-0 md:w-60 lg:w-40 w-full">
          <div className="absolute h-20 w-full grid content-end bg-[#000000]/80 z-10">
            <div className="flex relative justify-center">
              <h2 className="justify-self-end text-white text-lg">
                SCAN BARCODE
              </h2>
              <p className="absolute text-sm right-2 bottom-2">UPLOAD</p>
            </div>
          </div>
          <QrReader
            delay={this.state.delay}
            facingmode="rear"
            style={previewStyle}
            onError={this.handleError}
            onScan={this.handleScan}
          />
          <h1>{this.state.result ? "Succssed" : ""}</h1>
          <div className="absolute bottom-16 w-full py-3 z-10 opacity-70">
            <h2 className="mx-10 text-center text-lg bg-[#3B2B2F] rounded-lg text-[#E8E7D8]">
              NO BARCODE?
            </h2>
          </div>
        </article>
      </div>
    );
  }
}

export default QrScanner2;
