import React from "react";
import "../../index.css";
function ReviewWithoutImg({ item }) {
  const starsData = [
    item.vector,
    item.vector,
    item.vector,
    item.vector,
    item.vector2,
  ];

  return (
    <section className="grid place-items-center my-5 ">
      <div className="max-w-[30rem] w-full grid gap-5 px-4">
        {/* 
      ///////////Top Rectangle & Subtitles////////
      ////////////////////////// */}
        <div className="flex flex-row pt-5 gap-1 justify-start items-center">
          <div className="w-[4.58rem] h-[4.58rem] relative bg-[#E8E7D8]"></div>
          <div>
            <p className="text-3xl font-medium text-[#3B2B2F]">DOJA - C99</p>
            <p className="text-[#A7AE45] text-lg font-normal">
              LOT #: XXXXXXXXXXXX (REQUIRED)
            </p>
          </div>
        </div>
        <p className="text-4xl font-normal text-[#3B2B2F]">
          {item.secondeTitle}
        </p>

        {/* 
      ///////////Bottom Image & Titles////////
      ////////////////////////// */}

        <div className="">
          <p className="text-[#3B2B2F] text-2xl">{item.thirdTitle}</p>
          <div className="max-w-full bg-[#E8E7D8] max-h-[28rem] rounded-xl p-2 px-2 ">
            <p className="text-sm tracking-wider leading-5 font-normal ">
              {item.pargraph}
            </p>
            {/* stars */}
            <img src={item.imgUrl} className="mx-auto py-3" />
            <div className="flex justify-center items-center py-3">
              {starsData.map((star) => {
                <img key={star} src={star} alt="colored star" />;
              })}
            </div>
            <p className="text-center">RATE 1- 5</p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ReviewWithoutImg;
