import React from "react";
import { FaBars } from "react-icons/fa";
import { FaAngleRight } from "react-icons/fa";

function Profile() {
  const statsData = [
    {
      stateTitle: "YEARS AS BUDTENDER",
      stateNumber: "13",
      img: "/images/420GuruIcon.svg",
    },
    {
      stateTitle: "XP EARNED",
      stateNumber: "42069",
      img: "/images/420GuruIcon.svg",
    },
    {
      stateTitle: "STRAINS REVIEWED",
      stateNumber: "1017",
      img: "/images/420GuruIcon.svg",
    },
    {
      stateTitle: "BADGES EARNED",
      stateNumber: "710",
      img: "/images/420GuruIcon.svg",
    },
    {
      stateTitle: "CLASS",
      stateNumber: "WEED WIZARD",
      img: "/images/420GuruIcon.svg",
    },
  ];

  const statsCard = statsData.map((state) => (
    <article
      key={state.stateNumber}
      className="w-full py-1 px-5 bg-[#E8E7D8] rounded-xl flex items-center justify-between font-Roboto"
    >
      <span className="text-[#3B2B2F] text-sm font-bold">
        {state.stateTitle}
      </span>
      <span className="text-2xl text-[#3B2B2F]/70 font-bold">
        {state.stateNumber}
      </span>
    </article>
  ));

  return (
    <section className="grid place-items-center">
      <div className="max-w-[30rem] w-full">
        {/* 
      ///////////Profile////////
      ////////////////////////// */}

        <div
          style={{ backgroundImage: `url('/images/background.svg')` }}
          className="pb-3"
        >
          <div className="flex justify-end pr-4 pt-3 ">
            <FaBars className="text-white text-xl w-[22.75px]" />
          </div>
          {/* Photo */}
          <div className="flex flex-col items-center">
            <div className="relative">
              <img
                src="/images/BiggerL.svg"
                className="w-[35px] h-[38px] absolute left-[-12px] bottom-0"
              />
              <span className="absolute bottom-0 right-0">
                <span className="relative w-[21px] h-[21px] bg-[#FFFFFF] inline-block rounded-full">
                  <img
                    className="absolute right-1 left-[4.2px] top-1"
                    src="/images/plus.svg"
                  />
                </span>
              </span>
              <img
                src="/images/profileImg.svg"
                className="w-[6.4rem] h-[6.4rem] "
              />
            </div>
            {/* Name */}
            <p className="text-2xl text-[#FFFFFF] font-Bebas font-normal">
              Rich Navarro
            </p>
            <p className="text-base text-white/70 font-family font-normal">
              Tokyo Smoke - 333 Yonge
            </p>
          </div>
        </div>
        {/* End */}

        <div className="mt-6 mb-5">
          {/* Profile States */}

          <p className="text-[#3B2B2F] text-2xl place-items-start font-normal ml-4 mb-2">
            Profile stats
          </p>
          <div className="grid content-start justify-items-center gap-2 px-4 min-w-max">
            {statsCard}
          </div>

          {/* 
      ///////////Badges////////
      ////////////////////////// */}

          <div className="mt-3 px-4">
            <div className="flex justify-between items-center">
              <p className="text-[#3B2B2F] text-2xl font-normal">BADGES</p>
              <div className="flex items-center gap-1">
                <p className="text-sm  font-family text-black/70">View All</p>
                <FaAngleRight className=" text-[#111111]" />
              </div>
            </div>
          </div>
          {/* Photo badges */}
          <div className="flex gap-3 px-4 mt-3 overflow-x-clip">
            {statsData.map((state) => (
              <img key={state.stateNumber} src={state.img} alt="" />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export default Profile;
