import React from "react";
import { FaAngleLeft } from "react-icons/fa";
import { NavLink } from "react-router-dom";
function AddImage() {
  const cardData = [
    {
      mainTitle: "DESCRIPTION",
      subTitle: "Doja C99 Flower",
    },
    {
      mainTitle: "TAGS",
      subTitle: "#weed #doja #c99 #flower",
    },
  ];

  const card = cardData.map((data) => (
    <div
      key={data.mainTitle}
      className="w-full h-10 px-5 bg-[#E8E7D8] rounded-xl mb-2 flex items-center justify-between"
    >
      <span className="font-family text-xs  text-[#3B2B2F] font-bold">
        {data.mainTitle}
      </span>
      <span className="font-family text-xs font-bold text-[#3B2B2F]">
        {data.subTitle}
      </span>
    </div>
  ));

  return (
    <div className="grid place-items-center">
      <div className="max-w-[30rem] w-full mt-3 p-4 grid place-content-center">
        <div className="mb-8">
          {/* 
      ///////////Add Description////////
      ////////////////////////// */}
          <div className="flex w-full justify-center border-shadow relative">
            <div className="absolute left-0 top-1">
              <FaAngleLeft />
            </div>
            <p className="text-2xl font-normal justify-self-center">
              ADD DESCRIPTION
            </p>
          </div>
          {/* 
      ///////////Img & Cards & labls////////
      ////////////////////////// */}
          <div className="mt-11 grid content-center">
            <div>
              <img
                src="/images/ImageViewer.svg"
                className="max-w-full h-auto"
              />
            </div>
            <div className="mt-3">
              {card}
              <NavLink
                to="/"
                type="button"
                className="w-full mt-2 py-5 bg-[#A7AE45] rounded-xl text-center cursor-pointer"
              >
                <span className="text-[#FFFFFF] font-family font-bold text-2xl">
                  Add Another Image
                </span>
              </NavLink>{" "}
              <NavLink
                to={"../../spin"}
                type="button"
                className="w-full mt-2 py-5 bg-[#A7AE45] rounded-xl text-center cursor-pointer"
              >
                <span className="text-[#FFFFFF] font-family font-bold text-2xl">
                  Done
                </span>
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddImage;
