import React from "react";
import "../../index.css";
function Review({ item }) {
  const starsImg = [
    item.vector,
    item.vector,
    item.vector,
    item.vector,
    item.vector2,
  ];

  return (
    <section className="grid place-items-center my-5 px-1 ">
      <div className="sm:max-w-[22.375rem] flex flex-col justify-center gap-5 ">
        {/* ////////TopImage & subtitles////////
         */}
        <div className="flex flex-row pt-5 gap-1 justify-start items-center">
          <div className="max-w-[4.5rem] max-h-[4.5rem] relative">
            <img className="w-full block" src={item.rectangle} alt="" />
            <span className="w-5 h-5 bg-[#D9D9D9] rounded-full text-xs font-semibold inline-block text-center absolute right-1 bottom-1 py-0.5 font-family">
              6
            </span>
          </div>
          <div>
            <p className="text-3xl font-medium text-[#3B2B2F]">DOJA - C99</p>
            <p className="text-[#A7AE45] text-[1.15rem] font-normal">
              LOT# : 2202010809
            </p>
          </div>
        </div>
        {/* ///////////BottomImage & Titles////////
         */}
        <p className="text-4xl font-normal text-[#3B2B2F]">
          {item.secondeTitle}
        </p>
        <div>
          <p className="text-[#3B2B2F] text-[1.5rem]">{item.thirdTitle}</p>
          <div
            className="max-w-[22.375rem] bg-[#E8E7D8] max-h-[28.31rem] rounded-xl p-2
        "
          >
            <p className="text-sm tracking-wider w-72 font-normal">
              {item.pargraph}
            </p>
            <img src={item.imgUrl} className="mx-auto py-3" />

            {/* Stars */}

            <div className="flex justify-center items-center py-3">
              {starsImg.map((star) => {
                return (
                  <img key={Math.random()} src={star} alt="colored star" />
                );
              })}
            </div>
            <p className="text-center">RATE 1- 5</p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Review;
