import React from "react";
import { useState } from "react";
import SubmitModal from "../Modals/SubmitModal";
import data from "../../data";
import Card from "./Card";

function ReviewSummary() {
  const [open, setOpen] = useState(false);
  const cardData = [
    {
      imgUrl: "/images/Frame4.svg",
      mainTitle: "VISUAL",
      stars: [
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector2,
      ],
    },
    {
      imgUrl: "/images/Frame.png",
      mainTitle: "OLFACTORY",
      stars: [
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector,
      ],
    },
    {
      imgUrl: "/images/Frame2.png",
      mainTitle: "TASTE",
      stars: [
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector2,
      ],
    },
    {
      imgUrl: "/images/Frame3.png",
      mainTitle: "SMOKEABILITY",
      stars: [
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector,
      ],
    },
    {
      imgUrl: "/images/Group4.png",
      mainTitle: "EFFECT",
      stars: [
        data[0].vector,
        data[0].vector,
        data[0].vector,
        data[0].vector2,
        data[0].vector2,
      ],
    },
  ];

  return (
    <div className="grid place-items-center my-5 ">
      <div className="sm:max-w-[22.43rem] flex flex-col justify-center gap-5 ">
        {/* Top photo and subtitle */}

        <div className="flex flex-row pt-5 gap-1 justify-start items-center">
          <div className="max-w-[4.56rem] max-h-[4.56rem] relative">
            <img className="w-full block" src={data[0].rectangle} alt="" />
          </div>
          <div>
            <p className="text-[28px] font-medium text-[#3B2B2F]">DOJA - C99</p>
            <p className="text-[#A7AE45] mt-[-6px] text-[18px] font-normal">
              LOT# : 2202010809
            </p>
          </div>
        </div>
        {/* review summary section */}
        <p className="text-[1.9rem] text-[#3B2B2F] -mb-5">REVIEW SUMMARY</p>
        <div className="flex flex-col gap-3">
          {cardData.map((data) => (
            <Card key={data.mainTitle} data={data} />
          ))}
        </div>

        <button
          className="px-8 py-5 bg-[#3B2B2F] text-[#E8E7D8] rounded-xl text-2xl font-bold font-family text-center cursor-pointer"
          onClick={() => setOpen((prev) => !prev)}
        >
          Submit Review
        </button>
      </div>
      {/* submit modal */}
      <SubmitModal open={open} setOpen={setOpen} />
    </div>
  );
}

export default ReviewSummary;
