const Card = ({ data }) => {
  return (
    <section className="bg-[#E8E7D8] max-w-[25rem] max-h-[6.8rem] rounded-xl  flex flex-col items-center justify-center">
      <div className="max-w-[22.43rem] max-h-[6.37rem] flex p-10  items-center">
        <div>
          <img src={data.imgUrl} alt="" className="w-full" />
        </div>
        <div className="flex ml-7">
          <div className=" flex flex-col justify-center">
            <p className="text-[#3B2B2F] text-2xl ">{data.mainTitle}</p>
            <p className="text-[#5D5C56] text-sm font-family w-40 font-medium -mt-1">
              <span className="after:content-['.'] after:font-bold after:ml-1">
                4 Categories
              </span>
              18 Starts
            </p>
            <div className="flex gap-1 pt-1 ">
              {data.stars.map((star) => {
                return (
                  <img
                    key={Math.random()}
                    src={star}
                    className="w-[1.5rem] h-[1.4rem]"
                  />
                );
              })}
            </div>
          </div>
          <p className="text-[#3B2B2F]  text-sm font-semibold font-family mt-1">
            EDIT
          </p>
        </div>
      </div>
    </section>
  );
};

export default Card;
