import React, { useState } from "react";
import { QrReader } from "react-qr-reader";

const QrScanner = (props) => {
  const [data, setData] = useState("No result");

  return (
    <section className="min-h-screen">
      <div className="">
        <article className="fixed left-0 top-0 w-full">
          <div className="absolute h-20 w-full grid content-end bg-[#000000]/80 z-10">
            <div className="flex relative justify-center">
              <h2 className="justify-self-end text-white text-lg">
                SCAN BARCODE
              </h2>
              <p className="absolute text-sm right-2 bottom-2">UPLOAD</p>
            </div>
          </div>
          <div className="relative z-0 h-full w-full">
            <QrReader
              constraints={{ facingMode: "environment" }}
              onResult={(result, error) => {
                if (!!result) {
                  setData(result?.text);
                }

                if (!!error) {
                  console.info(error);
                }
              }}
              style={{ width: "100%" }}
            />
          </div>
          <div className="absolute bottom-10 w-full py-3 z-10 opacity-70">
            <h2 className="mx-10 text-center text-lg bg-[#3B2B2F] rounded-lg text-[#E8E7D8]">
              NO BARCODE?
            </h2>
          </div>
        </article>
      </div>
      {/* <p>{data}</p> */}
    </section>
  );
};

export default QrScanner;
