import React from "react";
import { FaBars } from "react-icons/fa";
import { NavLink } from "react-router-dom";
import data from "../../data";
function Product() {
  const starsImg = [
    "/images/proColoredStar.svg",
    "/images/proColoredStar.svg",
    "/images/proColoredStar.svg",
    "/images/proColoredStar.svg",
    "/images/proBorderStar.svg",
  ];

  const stars = starsImg.map((star) => (
    <img key={Math.random()} src={star} alt="" />
  ));

  return (
    <div className="grid place-items-center my-5 ">
      <div className="">
        <div className="flex justify-end">
          <FaBars className="text-xl" />
        </div>

        {/* 
      ///////////Product Section////////
      ////////////////////////// */}
        <div className="flex flex-col justify-center items-center mt-28">
          {/* product img */}
          <div className="w-[10rem] h-[10rem]">
            <img src={data[0].rectangle} className="rounded-full w-full" />
          </div>

          {/* 
      ///////////Product Descriptions////////
      ////////////////////////// */}
          <div className="flex flex-col justify-center items-center">
            <p className="text-[#3B2B2F] text-[3.56rem]">DOJA - C99</p>
            <p className="text-[#3B2B2F] text-[1.63rem] font-normal -mt-4">
              LOT:2202010809
            </p>
            <div className="flex gap-2">{stars}</div>
            <p className="text-[3.56rem] font-normal text-[#3B2B2F]">76/90</p>
            <p className="text-[1.63rem] text-[#3B2B2F] font-normal -mt-4 mb-2">
              OVERALL SCORE
            </p>
          </div>
          {/* 
      ///////////Button////////
      ////////////////////////// */}
          <NavLink
            to={"../spin"}
            className="px-[6.25rem] py-5 bg-[#A7AE45] text-[#fff] rounded-xl text-2xl font-bold font-family text-center cursor-pointer]"
          >
            Spin To Win
          </NavLink>
        </div>
      </div>
    </div>
  );
}

export default Product;
