export default function FormAction({
    handleSubmit,
    type='Button',
    action='submit',
    text
}){
    return(
        <>
        {type==='Button' ?
            <button type={action} className="group relative w-full flex justify-center py-2 px-8 border border-transparent text-2xl font-medium rounded-lg text-white bg-[#6c8932] hover:bg-[#7fa13d] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-400 mt-10" onSubmit={handleSubmit} >
                 {text} 
            </button>
            :
            <></>
        }
        </>
    )
}
