import Modal from "../products/Modal";

const CongratsModal = ({ setOpenModal, setOpenInfoModal }) => {
  return (
    <section>
      <Modal>
        <div className="flex flex-col items-center justify-center gap-3 max-w-[22.5rem] w-full">
          {/* title */}
          <p className="text-5xl break-all text-center font-normal leading-snug px-3">
            CONGRATULATIONS YOU WON A
          </p>
          {/* Shirt Img */}
          <div>
            <img className="max-w-full h-auto" src="/images/t-shirt.svg" />
          </div>
          {/* title */}
          <p className="text-2xl text-center font-normal leading-7">
            To claim your prize, tell us a little bit about yourself.
          </p>
          {/* button */}
          <div className="w-full px-7">
            <button
              className="w-full h-[4.3125rem] bg-[#A7AE45] text-[#fff] rounded-xl text-2xl font-bold font-family text-center cursor-pointer]"
              onClick={() => (setOpenModal(false), setOpenInfoModal(true))}
            >
              Next
            </button>
          </div>
        </div>
      </Modal>
    </section>
  );
};

export default CongratsModal;
